/* Minimized Taskbar Styling */
.taskbar.minimized a {
    display: flex;
    align-items: center;  /* Center vertically */
    justify-content: center;  /* Center horizontally */
    width: 60px;
    height: 60px;
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}

/* Icon within minimized taskbar */
.taskbar.minimized a i {
    font-size: 24px;  /* Adjust icon size */
    line-height: 60px;  /* Match the button height to center vertically */
    text-align: center;  /* Center horizontally */
    width: 100%;  /* Make the icon fill the width of the button */
    height: 100%;  /* Make the icon fill the height of the button */
    margin: 0;  /* Remove extra margins */
    padding: 0;  /* Remove extra padding */
    display: block;  /* Block display ensures full width/height control */
}

