// Toggle taskbar functionality (independent of flashcard rendering)
document.getElementById('toggle-taskbar').addEventListener('click', function () {
    const taskbar = document.getElementById('taskbar');
    const mainContent = document.getElementById('main-content');
    taskbar.classList.toggle('minimized');
    mainContent.classList.toggle('expanded');
});

// Flashcard-related functionality
const completionEl = document.getElementById("completion");
const prevButton = document.getElementById("previousCard");
const nextButton = document.getElementById("nextCard");
let activeCardIndex = 0;

// Fix: Handle data safely by falling back to an empty array if data is unavailable
const data = {{ completion | safe }} || [];

function renderFlashcards() {
    // Check if there are any flashcards to display
    if (data.length === 0) {
        prevButton.style.display = "none";
        nextButton.style.display = "none";
    } else {
        prevButton.style.display = "inline-block";
        nextButton.style.display = "inline-block";
        data.forEach((item, i) => {
            completionEl.innerHTML += `
            <div class="flashcard ${i === activeCardIndex ? "active-card" : ""}">
                <div class="flashcard-inner">
                    <div class="flashcard-front">
                        <div class="flashcard-content">${item.question}</div>
                    </div>
                    <div class="flashcard-back">
                        <div class="flashcard-content">${item.answer}</div>
                    </div>
                </div>
            </div>`;
        });
    }

    const cards = document.querySelectorAll('.flashcard');

    // Ensure each card can flip when clicked, but only if there are cards
    if (cards.length > 0) {
        cards.forEach((card, i) => {
            card.addEventListener('click', () => {
                card.classList.toggle('flip');
            });
        });
    }
}

const cardIndexEl = document.querySelector(".card-index");

function updateCardIndex() {
    const cards = document.querySelectorAll('.flashcard');
    if (cards.length > 0) {
        cardIndexEl.textContent = `${activeCardIndex + 1}/${cards.length}`;
    }
}

// Safely render the flashcards
renderFlashcards();
const cards = document.querySelectorAll('.flashcard');

// Ensure that the card index is updated only when there are flashcards
if (cards.length > 0) {
    updateCardIndex();

    // Handle the next and previous card functionality only if there are cards
    document.querySelector(".next-card").addEventListener("click", () => {
        const currentCard = cards[activeCardIndex];
        currentCard.classList.remove("active-card");
        activeCardIndex++;
        if (activeCardIndex >= cards.length) {
            activeCardIndex = 0;
        }
        updateCardIndex();
        cards[activeCardIndex].classList.add("active-card");
    });

    document.querySelector(".previous-card").addEventListener("click", () => {
        const currentCard = cards[activeCardIndex];
        currentCard.classList.remove("active-card");
        activeCardIndex--;
        if (activeCardIndex < 0) {
            activeCardIndex = cards.length - 1;
        }
        updateCardIndex();
        cards[activeCardIndex].classList.add("active-card");
    });
}

