    padding: 0; /* Remove extra padding */
    height: 60px; /* Ensure uniform button height */
    box-sizing: border-box; /* Include padding in element's width and height */
}

/* Toggle button when minimized */
.taskbar.minimized a#toggle-taskbar span {
    display: none; /* Hide the text when minimized */
}

.taskbar.minimized a i {
    font-size: 24px; /* Keep the icon size the same */
    margin-right: 0; /* No extra margin needed */
}

.taskbar.minimized a span {
    display: none; /* Hide the text when minimized */
}

